import React, { useState, useEffect } from "react";
import Amplify, { Auth, Storage } from "aws-amplify";
import Sidenav from "../../containers/SideNav";
import { useHistory } from "react-router-dom";
import {
  HelpBlock,
  FormGroup,
  FormControl,
  FormLabel,
  Row,
  Button
} from "react-bootstrap";
import LoaderButton from "../../components/LoaderButton";
import { useFormFields } from "../../libs/hooksLib";
import { onError } from "../../libs/errorLib";

import config from '../../aws-config';

Amplify.configure({
  "aws_appsync_graphqlEndpoint": "https://qssh4niq5bgujocnsbpv2zg7am.appsync-api.us-east-1.amazonaws.com/graphql",
  "aws_appsync_region": "us-east-1",
  "aws_appsync_authenticationType": "AMAZON_COGNITO_USER_POOLS",
  Auth: {
    region: config.aws_cognito_region,
    userPoolId: config.aws_user_pools_id,
    identityPoolId: config.aws_cognito_identity_pool_id,
    userPoolWebClientId: config.aws_user_pools_client_id
  },    
  Storage: {
    bucket: config.aws_s3_bucket, //REQUIRED -  Amazon S3 bucket
    region: config.aws_s3_bucket_region, //OPTIONAL -  Amazon service region
    identityPoolId: config.aws_cognito_identity_pool_id
  }
});


export default function UpdateProfile() {
  const history = useHistory();
  const [codeSent, setCodeSent] = useState(false);
  const [profile, setProfile] = useState({
    email: "",
    name: "",
    given_name: "",
    nickname: "",
  });

  const [isConfirming, setIsConfirming] = useState(false);
  const [isSendingCode, setIsSendingCode] = useState(false);

  const onLoad = async () => {
    try {
      const user = await Auth.currentUserInfo();
      console.log(user)
      setProfile({
        email: user.attributes.email,
        name: user.attributes.name,
        given_name: user.attributes.given_name,
        nickname:user.attributes.nickname,
        locale:user.attributes.locale,
        address:user.attributes.nickname
      });
    } catch(e) {

    }
    
  }
  useEffect(()=>{
    onLoad();
    }, []);
    
    const onSubmit = async data => {
      const user = await Auth.currentAuthenticatedUser();
      
      await Auth.updateUserAttributes(user, { 
        email: profile.email,
        name: profile.name,
        given_name: profile.given_name,
        nickname: profile.nickname,
        locale: profile.locale,
        address: profile.address
      });console.log(user)
    };
    const onClickHandler = (e) => {
      history.push('/profile')
    }
return (
  <div className="next-steps my-5 content-wrapper">
    <Sidenav />
  <div className="main-content-update">
    <form onSubmit={onSubmit}>
      <Row>
        <FormGroup bsSize="large" controlId="email">
        <FormLabel>Email</FormLabel>
        <FormControl
          autoFocus
          value={profile.email}
          name="email"
          type="text"
          onChange={e => {
            setProfile({ ...profile, email: e.target.value });
          }}
        />
      </FormGroup>
      <FormGroup bsSize="large" controlId="name">
        <FormLabel>Name</FormLabel>
        <FormControl
          autoFocus
          value={profile.name}
          name="name"
          type="text"
          onChange={e => {
            setProfile({ ...profile, name: e.target.value });
          }}
        />
      </FormGroup>
      </Row>
      <Row>
        <FormGroup bsSize="large" controlId="profile.given_name">
        <FormLabel>Bank Name</FormLabel>
        <FormControl
          autoFocus
          value={profile.given_name}
          name="bank"
          type="text"
          onChange={e => {
            setProfile({ ...profile, given_name: e.target.value });
          }}
        />
      </FormGroup>
      <FormGroup bsSize="large" controlId="nickname">
        <FormLabel>Title</FormLabel>
        <FormControl
          autoFocus
          value={profile.nickname}
          name="title"
          type="text"
          onChange={e => {
            setProfile({ ...profile, nickname: e.target.value });
          }}
        />
      </FormGroup>
      </Row>
      <Row>
        <FormGroup bsSize="large" controlId="profile.locale">
        <FormLabel>City</FormLabel>
        <FormControl
          autoFocus
          value={profile.locale}
          name="city"
          type="text"
          onChange={e => {
            setProfile({ ...profile, locale: e.target.value });
          }}
        />
      </FormGroup>
      <FormGroup bsSize="large" controlId="profile.address">
        <FormLabel>State</FormLabel>
        <FormControl
          autoFocus
          value={profile.address}
          name="state"
          type="text"
          onChange={e => {
            setProfile({ ...profile, address: e.target.value });
          }}
        />
      </FormGroup>
      </Row>
      <LoaderButton
        block
        type="submit"
        bsSize="large"
        isLoading={isSendingCode}
        /* onClick={onClickHandler} */
        /* disabled={!validateEmailForm()} */
      >
        Update Profile
      </LoaderButton>
    </form>
  </div>
    
  </div>     
  );
};
